
// When the user clicks on the button, toggle between hiding and showing the dropdown content.
function myFunction() {
    document.getElementById("myDropdown").classList.toggle("show");
}

// Close the dropdown if the user clicks outside of it.
window.onclick = function(event) {
  if (!event.target.matches('.dropbtn')) {

    var dropdowns = document.getElementsByClassName("dropdown-content");
    var i;
    for (i = 0; i < dropdowns.length; i++) {
      var openDropdown = dropdowns[i];
      if (openDropdown.classList.contains('show')) {
        openDropdown.classList.remove('show');
      }
    }
  }
}

// Get time via API call.
$(document).ready(function() {
    console.log("Getting alarm settings.");
    $.ajax({
        url: "/time"
    }).then(function(data) {
        var alarm_time = data.time;
        document.getElementById("alarm_time").value = alarm_time;
    });
});

// Update time via API call.
function update_alarm() {
    console.log("Updating alarm settings.");
    var xhttp = new XMLHttpRequest();
    var alarm = document.getElementById("alarm_time").value;
    xhttp.open("POST", "/time", true);
    xhttp.send("time=" + alarm);
}

// Update time before leaving pate.
$(window).bind('beforeunload', function(){
    console.log("Updating alarm with current settings before leaving page.");
    update_alarm();
});
